import React from 'react';
import PropTypes from 'prop-types';
import classNames from 'classnames';
import ActivityPost from '@ciscospark/react-component-activity-post';
import ActivityShareFiles from '@ciscospark/react-component-activity-share-files';
import ActivitySystemMessage, {SYSTEM_MESSAGE_VERBS} from '@ciscospark/react-component-activity-system-message';
import {hasAdaptiveCard, WIDGET_ADAPTIVE_CARD_ON, cards} from '@ciscospark/react-component-utils';
import AdaptiveCard from '@ciscospark/react-component-adaptive-card';

import styles from './styles.css';

const POST_VERB = 'post';
const SHARE_VERB = 'share';
const LINKS_CONTENT_CATEGORY = 'links';


const propTypes = {
  actor: PropTypes.shape({
    displayName: PropTypes.displayName
  }).isRequired,
  activity: PropTypes.shape({
    contentCategory: PropTypes.string,
    content: PropTypes.string,
    displayName: PropTypes.string,
    files: PropTypes.shape({
      items: PropTypes.arrayOf(PropTypes.shape({
        image: PropTypes.shape({
          url: PropTypes.string
        }),
        thumbnail: PropTypes.string,
        mimeType: PropTypes.string,
        url: PropTypes.string
      }))
    })
  }).isRequired,
  timestamp: PropTypes.string.isRequired,
  isAdditional: PropTypes.bool,
  verb: PropTypes.string.isRequired,
  adaptiveCardFeatureState: PropTypes.string.isRequired,
  sparkInstance: PropTypes.object
};

const defaultProps = {
  isAdditional: false,
  sparkInstance: {}
};

/**
 * It will store the links and the names of the file and display them
 * as link components separated by commas
 * @param {Object} props
 * @returns {Object} Activity post Item
 */
function createECMItemComponents(props) {
  const links = [];
  /* eslint-disable-next-line react/prop-types */
  const {activity} = props;
  let content = 'This ECM file is not supported';

  if (activity.links && activity.links.items) {
    for (const item of activity.links.items) {
      links.push(`<a href="${item.sslr.loc}">${item.displayName}</a>`);
    }

    content = activity.displayName
      ? `${links.join('\n')} \n ${activity.displayName}`
      : `${links.join('\n')}`;
  }

  return (
    <ActivityPost
      content={content}
      displayName={activity.displayName}
      renderedComponent={activity.component}
      {...props}
    />
  );
}

function ActivityItem(props) {
  const {
    activity,
    isAdditional,
    verb,
    adaptiveCardFeatureState,
    sparkInstance
  } = props;

  let itemComponent = '';
  let shouldRenderAdaptiveCard = false;

  if (Object.prototype.hasOwnProperty.call(activity, cards)) {
    shouldRenderAdaptiveCard = hasAdaptiveCard(activity.cards, sparkInstance);
  }
  if (verb === POST_VERB) {
    itemComponent = (shouldRenderAdaptiveCard && adaptiveCardFeatureState === WIDGET_ADAPTIVE_CARD_ON)
      ? <AdaptiveCard cards={activity.cards} displayName={activity.displayName} /> : (<ActivityPost
        content={activity.content}
        displayName={activity.displayName}
        renderedComponent={activity.component}
        {...props}
      />);
  }
  else if (verb === SHARE_VERB) {
    if (activity.contentCategory === LINKS_CONTENT_CATEGORY) {
      // ECM files will be displayed as links for now
      itemComponent = createECMItemComponents(props);
    }
    else {
      itemComponent = (
        <ActivityShareFiles
          renderAdaptiveCard={(shouldRenderAdaptiveCard && adaptiveCardFeatureState === WIDGET_ADAPTIVE_CARD_ON
            ) ? shouldRenderAdaptiveCard : !shouldRenderAdaptiveCard}
          cards={activity.cards}
          content={activity.content}
          displayName={activity.displayName}
          files={activity.files.items}
          {...props}
        />
      );
    }
  }
  else if (SYSTEM_MESSAGE_VERBS.indexOf(verb) !== -1) {
    itemComponent = <ActivitySystemMessage {...props} />;
  }

  return (
    <div className={classNames('ciscospark-activity-item-container', styles.activityItemContainer, isAdditional ? styles.additional : '')}>
      {itemComponent}
    </div>
  );
}

ActivityItem.propTypes = propTypes;
ActivityItem.defaultProps = defaultProps;

export default ActivityItem;
