import React from 'react';
import PropTypes from 'prop-types';
import classNames from 'classnames';
import {bytesToSize, getFileIcon} from '@ciscospark/react-component-utils';
import ShareFile from '@ciscospark/react-component-activity-share-file';

import {
  Button,
  ContentItem,
  Icon
} from '@collab-ui/react';

import styles from './styles.css';

const propTypes = {
  actor: PropTypes.object.isRequired,
  file: PropTypes.shape({
    displayName: PropTypes.string,
    fileSize: PropTypes.number,
    objectType: PropTypes.string
  }).isRequired,
  isFetching: PropTypes.bool,
  isPending: PropTypes.bool,
  objectUrl: PropTypes.string,
  onDownloadClick: PropTypes.func.isRequired,
  timestamp: PropTypes.string.isRequired,
  type: PropTypes.string
};

const defaultProps = {
  isFetching: false,
  isPending: false,
  objectUrl: '',
  type: 'chat'
};

function ActivityShareThumbnail(props) {
  const {
    actor,
    file,
    isFetching,
    isPending,
    objectUrl,
    onDownloadClick,
    timestamp,
    type
  } = props;

  const {
    displayName,
    fileSize,
    mimeType
  } = file;

  function handleDownloadClick() {
    onDownloadClick(file);
  }

  const actionNode
  = (
    <div>
      <Button
        ariaLabel="Download"
        style={{backgroundColor: 'black'}}
        circle
        onClick={handleDownloadClick}
        size={32}
      >
        <Icon name="icon-arrow-tail-down_12" color="white" />
      </Button>
    </div>
  );

  const isGif = mimeType === 'image/gif';

  if (type === 'file') {
    return (
      <div className={classNames('ciscospark-activity-share-item', styles.shareItem)}>
        <div className={classNames('ciscospark-share-thumbnail', styles.thumbnail)}>
          <ContentItem
            actionNode={isPending ? '' : actionNode}
            content={objectUrl}
            height={file.image ? file.image.height : ''}
            icon={!file.image ? `icon-${getFileIcon(file.mimeType, 72)}` : ''}
            gifIcon={isGif ? 'icon icon-gif_20' : ''}
            fileSize={bytesToSize(fileSize)}
            loading={isFetching}
            loadingText="Loading"
            title={displayName}
            type={type}
            subtitle={`${actor.displayName}, ${timestamp}`}
            width={file.image ? file.image.width : ''}
          />
        </div>
      </div>
    );
  }
  if (file.image && type === 'chat') {
    return (
      <ContentItem
        actionNode={isPending ? '' : actionNode}
        content={objectUrl}
        height={file.image.height}
        gifIcon={isGif ? 'icon icon-gif_20' : ''}
        fileSize={bytesToSize(fileSize)}
        loading={isFetching}
        loadingText="Loading"
        title={displayName}
        type={type}
        width={file.image.width}
      />
    );
  }
  return (
    <ShareFile
      file={file}
      isPending={isPending}
      onDownloadClick={onDownloadClick}
    />
  );
}

ActivityShareThumbnail.propTypes = propTypes;
ActivityShareThumbnail.defaultProps = defaultProps;

export default ActivityShareThumbnail;
