import React from 'react';
import PropTypes from 'prop-types';
import classNames from 'classnames';
import Icon, {ICON_TYPE_ADD} from '@ciscospark/react-component-icon';

import styles from './styles.css';

export default function AddFileButton(props) {
  const {
    onChange
  } = props;

  return (
    <div className={classNames(`ciscospark-add-file-container`, styles.container)}>
      <button className={classNames(`ciscospark-add-file-button`, styles.button)}>
        <span className={styles.icon}><Icon type={ICON_TYPE_ADD} /></span>
      </button>
      <input
        className={classNames(`ciscospark-file-input`, styles.fileInput)}
        multiple="multiple"
        onChange={onChange}
        type="file"
      />
    </div>
  );
}

AddFileButton.propTypes = {
  onChange: PropTypes.func
};
