import React from 'react';
import {Provider} from 'react-redux';
import {createComponentWithIntl} from '@ciscospark/react-test-utils';

import ActivityList from '.';

import store from './__fixtures__/mock-store';

describe('ActivityList', () => {
  const onActivityDelete = jest.fn();
  const onActivityFlag = jest.fn();

  it('renders properly', () => {
    const component = createComponentWithIntl(<Provider store={store}>
      <ActivityList
        onActivityDelete={onActivityDelete}
        onActivityFlag={onActivityFlag}
      />
                                              </Provider>);

    const widget = component.toJSON();

    expect(widget).toMatchSnapshot();
  });
});
