import {bindActionCreators} from 'redux';
import {connect} from 'react-redux';
import {compose, lifecycle, withHandlers} from 'recompose';

import {
  acceptIncomingCall,
  placeCall,
  declineIncomingCall,
  hangupCall
} from '@ciscospark/redux-module-media';
import {createNotification} from '@ciscospark/react-container-notifications';
import {addError, removeError} from '@ciscospark/redux-module-errors';
import {constructHydraId, hydraTypes} from '@ciscospark/react-component-utils';

import messages from '../messages';
import {
  updateWidgetStatus,
  storeMeetDetails
} from '../actions';

import {destinationTypes} from '../';


function catchCallError(props) {
  return (e) => {
    const callErrorId = 'spark.call';
    const {formatMessage} = props.intl;
    const actionTitle = formatMessage(messages.okButtonLabel);
    let displayTitle;
    let displaySubtitle;
    switch (e.name) {
      case 'DevicesNotFoundError':
        displayTitle = formatMessage(messages.callErrorDeviceNotFound);
        break;
      case 'NotAllowedError':
        displayTitle = formatMessage(messages.callErrorNotAllowed);
        break;
      case 'SecurityError':
        displayTitle = formatMessage(messages.callErrorSecurity);
        break;
      default:
        displayTitle = formatMessage(messages.callErrorUnknownMessage);
        displaySubtitle = `(${e.name} ${e.message})`;
    }
    props.addError({
      actionTitle,
      code: e.name,
      id: callErrorId,
      displayTitle,
      displaySubtitle,
      onAction: () => props.removeError(callErrorId),
      temporary: true
    });
  };
}

function handleAnswer(props) {
  return () => {
    props.acceptIncomingCall(props.callInstance).catch(props.catchCallError);
  };
}

function handleDecline(props) {
  return () => {
    const {callInstance} = props;
    props.declineIncomingCall(callInstance);
  };
}

function handleHangup(props) {
  return () => {
    const {
      callInstance,
      widgetMeet
    } = props;
    props.hangupCall({call: callInstance, id: widgetMeet.callId});
  };
}

function handleCall(props) {
  return () => {
    const {
      sparkInstance,
      widgetMeet
    } = props;
    const {
      toType,
      toValue
    } = widgetMeet;

    let destination = toValue;
    const callOptions = {
      constraints: {
        audio: true,
        video: true
      },
      offerOptions: {
        offerToReceiveVideo: true,
        offerToReceiveAudio: true
      }
    };

    switch (toType) {
      case destinationTypes.USERID:
        destination = constructHydraId(hydraTypes.PEOPLE, toValue);
        break;
      case destinationTypes.SPACEID:
        destination = constructHydraId(hydraTypes.ROOM, toValue);
        break;
      case destinationTypes.PSTN:
        callOptions.constraints.video = false;
        callOptions.offerOptions.offerToReceiveVideo = false;
        break;
      default:
        break;
    }
    props.placeCall(sparkInstance, {destination, options: callOptions})
      .then(({id}) => {
        props.storeMeetDetails({callId: id});
      })
      .catch(props.catchCallError);
    props.updateWidgetStatus({hasInitiatedCall: true});
  };
}

function handleCallNotification(props) {
  return (incomingCall, fromPersonName) => {
    const {
      intl,
      avatarImage
    } = props;
    const {formatMessage} = intl;
    const details = {
      username: fromPersonName,
      message: formatMessage(messages.incomingCallMessage),
      avatar: avatarImage
    };
    props.createNotification(incomingCall.id, details);
  };
}

export default compose(
  connect(
    null,
    (dispatch) => bindActionCreators({
      createNotification,
      acceptIncomingCall,
      declineIncomingCall,
      placeCall,
      hangupCall,
      updateWidgetStatus,
      storeMeetDetails,
      addError,
      removeError
    }, dispatch)
  ),
  withHandlers({
    handleAnswer,
    handleDecline,
    handleHangup,
    handleCall,
    handleCallNotification,
    catchCallError
  }),
  lifecycle({
    componentWillReceiveProps(nextProps) {
      const {
        call,
        displayName,
        widgetMeet,
        currentUser,
        startCall,
        isIncoming
      } = nextProps;
      if (call) {
        const prevCall = this.props.call;

        // Check if this is a new initiated call and is incoming
        if (
          call.isInitiated &&
          (!prevCall || !prevCall.isInitiated) &&
          isIncoming
        ) {
          nextProps.handleCallNotification(call, displayName);
        }
      }

      if (
        (widgetMeet.toValue || call) && currentUser && !widgetMeet.status.hasOpenWithCall
        && (startCall === true || startCall === '' || startCall === 'true')
      ) {
        nextProps.updateWidgetStatus({hasOpenWithCall: true});
        nextProps.handleCall();
      }
    },
    componentWillUnmount() {
      if (this.props.isActive) {
        this.props.handleHangup();
      }
    }
  })
);
