
import React from 'react';
import ReactDOM from 'react-dom';
import {IntlProvider} from 'react-intl';
import {CookiesProvider} from 'react-cookie';
import injectTapEventPlugin from 'react-tap-event-plugin';
import MuiThemeProvider from 'material-ui/styles/MuiThemeProvider';
import getMuiTheme from 'material-ui/styles/getMuiTheme';
import {blue600, green800} from 'material-ui/styles/colors';
import '@ciscospark/react-component-spark-fonts';

import DemoWidgetRecents from './components/demo-widget-recents';

if (process.env.NODE_ENV !== 'production') {
  // eslint-disable-next-line global-require
  require('@ciscospark/widget-recents');
}

const muiTheme = getMuiTheme({
  palette: {
    primary1Color: blue600,
    accent1Color: green800
  }
});

injectTapEventPlugin();

ReactDOM.render(
  <MuiThemeProvider muiTheme={muiTheme}>
    <IntlProvider locale="en">
      <CookiesProvider>
        <DemoWidgetRecents />
      </CookiesProvider>
    </IntlProvider>
  </MuiThemeProvider>,
  document.getElementById('main')
);
